    "type": "command",
    "command": "input=$(cat); cwd=$(echo \"$input\" | jq -r '.workspace.current_dir'); dir=$(basename \"$cwd\"); if git -C \"$cwd\" rev-parse --git-dir > /dev/null 2>&1; then branch=$(git -C \"$cwd\" --no-optional-locks branch --show-current 2>/dev/null || echo 'detached'); if [ -n \"$(git -C \"$cwd\" --no-optional-locks status --porcelain 2>/dev/null)\" ]; then dirty='✗'; else dirty=''; fi; git_info=\" git:($branch) $dirty\"; else git_info=''; fi; printf '\\033[1;32m➜\\033[0m \\033[36m%s\\033[0m%s' \"$dir\" \"$git_info\""
