{
  "statusLine": {
    "type": "command",
    "command": "input=$(cat); cwd=$(echo \"$input\" | jq -r '.workspace.current_dir'); dir_name=$(basename \"$cwd\"); git_info=\"\"; if git -C \"$cwd\" rev-parse --git-dir > /dev/null 2>&1; then branch=$(git -C \"$cwd\" --no-optional-locks symbolic-ref --short HEAD 2>/dev/null || git -C \"$cwd\" --no-optional-locks rev-parse --short HEAD 2>/dev/null); if [ -n \"$branch\" ]; then if ! git -C \"$cwd\" --no-optional-locks diff --quiet 2>/dev/null || ! git -C \"$cwd\" --no-optional-locks diff --cached --quiet 2>/dev/null; then git_info=\" $(printf '\\033[1;34m')git:$(printf '\\033[31m')${branch}$(printf '\\033[34m']))$(printf '\\033[33m') ✗$(printf '\\033[0m')\"; else git_info=\" $(printf '\\033[1;34m')git:$(printf '\\033[31m')${branch}$(printf '\\033[34m'))$(printf '\\033[0m')\"; fi; fi; fi; printf '\\033[1;32m➜\\033[0m  \\033[36m%s\\033[0m%s' \"$dir_name\" \"$git_info\""
  },
  "enabledPlugins": {
    "exa-mcp-server@exa-mcp-server": true,
